mapper.select:
select p.*, i.image_save_name, i.image_type, i.image_name from product_table P inner join product_img i on p.product_no = i.product_no where product_category = #{category} and image_type = 'N'
and product_skintype in
#{sType}
and product_brand in
#{sBrand}
order by product_regdate desc
